const bcrypt = require("bcrypt");
const jwt = require("jsonwebtoken");
const express = require("express");
const session = require("express-session");
const aws = require("aws-sdk");
const multer = require("multer");
const multerS3 = require("multer-s3");

var Userdb = require("../models/model");
const Favourites = require("../models/favourites");
const Items = require("../models/items");

const sessionRouter = express.Router();

sessionRouter.use(
  session({
    key: "email",
    secret: "cmpe273_kafka_passport_mongo",
    resave: false, // Forces the session to be saved back to the session store, even if the session was never modified during the request
    saveUninitialized: false, // Force to save uninitialized session to db. A session is uninitialized when it is new but not modified.
    // duration: 60 * 60 * 1000, // Overall duration of Session : 30 minutes : 1800 seconds
    activeDuration: 5 * 60 * 1000,
    cookie: {
      expiresIn: 60 * 60 * 24,
    },
  })
);

//connect to s3 bucket

const s3 = new aws.S3({
  accessKeyId: process.env.S3_ACCESS_KEY,
  secretAccessKey: process.env.S3_SECRET_ACCESS_KEY,
  region: process.env.S3_BUCKET_REGION,
});

//create and save new user
exports.create = async (req, res) => {
  console.log("In register post");
  //validate request
  if (!req.body)
    return res.status(400).send({ message: "Content can not be empty" });

  //new user
  const hashedPassword = await bcrypt.hash(req.body.password, 10);

  const user = new Userdb({
    username: req.body.username,
    email: req.body.email,
    password: hashedPassword,
    shopName: "Shop_" + new Date().getTime(),
  });

  const savedUser = await user.save();
  if (savedUser) return res.send({ success: true, data: savedUser });
  else return res.status(500).send({ success: false });
  // save user in the db
  // user
  //   .save()
  //   .then((data) => {
  //     res.send(data);
  //   })
  //   .catch((err) => {
  //     console.log(err);
  //     res.status(500).send({ message: "some error occured" });
  //   });
};

exports.findUser = (req, res) => {
  console.log("In finduser post");

  var email = req.body.email;
  var password = req.body.password;

  console.log(email + " " + password);
  Userdb.findOne({ email: email }).then((user) => {
    console.log(user + "--------------------------");
    if (user) {
      console.log(" user exists");

      bcrypt.compare(password, user.password, (err, result) => {
        if (err) {
          console.log(err);
          res.send({ error: err });
        }
        if (result) {
          res.cookie("user", user.username, {
            maxAge: 900000,
            httpOnly: false,
            path: "/",
          });
          session.user = user;
          const payload = { _id: user._id, username: user.username };
          let token = jwt.sign(payload, process.env.SECRET, {
            expiresIn: "1h",
          });

          res.cookie("token", token, {
            maxAge: 900000,
            httpOnly: false,
            path: "/",
          });

          console.log("result " + result + " token " + token);

          res.send({ success: true, user, token });
          // res.send(result);
          console.log("=========end =============");
        } else {
          res.send({
            message: "Password doesn't match",
          });
        }
      });
    } else {
      console.log("No user exists");
      res.send({ message: "No user found!" });
    }
  });
};

exports.getSignIn = (req, res) => {
  console.log("---------------session user----------------");
  if (session.user) {
    console.log(session.user);
    console.log("---------------session user exist----------------");
    res.send({ loggedIn: true, user: req.session.user });
